import { getCaption } from "./caption-helper.js";
const style = `
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  margin-right: 1rem;
  margin-top: 1rem;
`;
const renderCaptionedBox = ({
  showcaseClass,
  showcaseStyle,
  captionWidth,
  componentClass,
  hasCheckeredBackground = false
}) => `
<div style="${style}">
  <dockit-box
    ${hasCheckeredBackground ? "checkered-background" : ""}
    class-name="${showcaseClass} ${componentClass}"
    showcase-style="${showcaseStyle}"
  ></dockit-box>
  <dockit-clipboard
    width="${captionWidth}"
  >${getCaption(showcaseClass, showcaseStyle)}</dockit-clipboard>
</div>`;
export { renderCaptionedBox };
