import { getCaption } from "./caption-helper.js";
const style = `
  display: flex;
  flex-direction: row;
  align-items: center;
  margin-top: 1rem;
`;
const renderCaptionedText = ({
  showcaseClass,
  showcaseStyle,
  captionWidth,
  hasLongText = false
}) => `
<div style="${style}">
  <dockit-clipboard
    width="${captionWidth}"
  >${getCaption(showcaseClass, showcaseStyle)}</dockit-clipboard>
  <dockit-text
    ${hasLongText ? "long-text" : ""}
    class="${showcaseClass}"
    style="${showcaseStyle}"
  ></dockit-text>
</div>`;
export { renderCaptionedText };
